import React from 'react'
import {useState} from 'react';
import Form from '../../pages/Form';
const time = ['10:00AM-12:00PM','2:00PM-4:00PM','5:00PM-7:00PM']

function Times(props) {

 const [event, setEvent] = useState(null)
 const [info, setInfo] = useState(false)
const[dispTime,setdispTime]=useState(null);
const [dispDate,setdispDate]=useState(null);
 function displayInfo(e) {
   setInfo(true);
   setEvent(e.target.innerText);
}
const finallysorted=()=>{
  if(info)
  {
    setdispTime(event);
    setdispDate(props.date.toDateString());
   
  }
}
return (
 <>
 <div className="times">
   {time.map(times => {
    return (
    <div>
      <button onClick={(e)=> displayInfo(e)}> {times} </button>
    </div>
        )
     })}
    <div>
      {info ? `Your appointment is set to ${event} ${props.date.toDateString()} ` : null}
      <button className="submitted" onClick={finallysorted} >Go Back</button>
      console.log({dispTime});
    console.log({dispDate})
    </div>
 </div>
 <Form dispTime={dispTime} dispDate={dispDate}/>
 </>
  )
}

export default Times;
