import React from 'react'
import {useState} from 'react';
import {Link} from "react-router-dom"
import Form from '../../pages/Form';
const time = ['10:00AM-12:00PM','2:00PM-4:00PM','5:00PM-7:00PM']

function Times(props) {

 const [event, setEvent] = useState(null)
 const [info, setInfo] = useState(false)
const[dispTime,setdispTime]=useState('');
const [dispDate,setdispDate]=useState('');
 function displayInfo(e) {
   setInfo(true);
   setEvent(e.target.innerText);
}
const finallysorted = () => {
  if (info) {
    console.log('event:', event); // Check the value of event
    console.log('props.date:', props.date.toDateString()); // Check the value of props.date
    setdispTime(event);
    setdispDate(props.date.toDateString());
  }
};

return (
 <>
 <div className="times">
   {time.map(times => {
    return (
    <div>
      <button onClick={(e)=> displayInfo(e)}> {times} </button>
    </div>
        )
     })}
    <div>
      {info ? `Your appointment is set to ${event} ${props.date.toDateString()} ` : null}
      <button className="submitted" onClick={finallysorted} ><Link to="/form">Go Back</Link></button>
     
    </div>
 </div>
 <Form dispTime={dispTime} dispDate={dispDate} />

 </>
  )
}

export default Times;
