import React from 'react';
import { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import Form from '../../pages/Form';

const time = ['10:00AM-12:00PM', '2:00PM-4:00PM', '5:00PM-7:00PM'];

function Times(props) {
  const [event, setEvent] = useState(null);
  const [info, setInfo] = useState(false);
  const [dispTime, setDispTime] = useState('');
  const [dispDate, setDispDate] = useState('');
  const history = useHistory(); // Get the history object

  function displayInfo(e) {
    setInfo(true);
    setEvent(e.target.innerText);
  }

  return (
    <>
      <div className="times">
        {time.map((times, index) => {
          return (
            <div key={index}>
              <button onClick={(e) => displayInfo(e)}> {times} </button>
            </div>
          );
        })}
        <div>
          {info ? `Your appointment is set to ${event} ${props.date.toDateString()} ` : null}
          {/* Use useHistory to navigate back and pass state using state object */}
          <button className="submitted" onClick={() => history.push('/form', { dispTime: event, dispDate: props.date.toDateString() })}>
            Go Back
          </button>
        </div>
      </div>
    </>
  );
}

export default Times;
